  if (props.layout === 'fullscreen') return 'wide'
  return 'prose'
})

const stackVariant = computed(() => {
  switch (props.layout) {
    case 'minimal': return 'compact'
    case 'compact': return 'default'
    default: return 'spacious'
  }
})








































































































































