} as const

// Updated: More flexible generic function
export function groupByCategory<T extends Record>(
  items: T[] | null | undefined,
): Record {
  if (!items) return {}

  return items.reduce((grouped: Record, item) => {
    // Use 'category' property if it exists, otherwise 'uncategorized'
    const category = (item.category as string) || 'uncategorized'
    if (!grouped[category]) {
      grouped[category] = []
    }
    grouped[category].push(item)
    return grouped
  }, {})
}



